import Menu from './Menulayout'
import  $ from 'jquery'
import { useState } from 'react';

function Search({favors,setFavors}) {
    sessionStorage.setItem('elems',[])
    const [count,sCount]=useState(0)
    const [flag,setFlag]=useState(false)
    const [visible,setVisible]=useState({display: 'none'})
    const [drag,setDrag]=useState(null)
    const [element,setElement]=useState(elems(5));
    function elems(c){
        let arr=[];
        
        for(let i=0;i<c;i++){
            arr.push(<div draggable="true"
                data-key={i} 
                onDragStart={(e)=>{
                setDrag(e);
                setVisible({display: 'block'})}} 
                onDragEnd={()=>{setVisible({display: 'none'})}}
                key={i}>{i+1}
                </div>)
        }
        return arr
    }
    return (
    <div className='searchBody'>
        <div className="mmm">
            {element}
        </div>
        <div className="kk" style={visible} onDragEnter={()=>{
            if(flag===true){
                sCount(count+1);
                const y=element
                delete y[drag.target.getAttribute('data-key')]
                setElement(y)
                setFavors(drag.target)
            }

        }}>{count}</div>
    </div>)

}
export default Search;



