import  $ from 'jquery'
import { useState } from 'react';

function Search() {
    const [count,sC]=useState(0)
    const [drag,setDrag]=useState(null)
    const [fav,setFav]=useState(elems(5));
    function elems(c){
        let arr=[];
        
        for(let i=0;i<c;i++){
            arr.push(<div draggable="true" onDragStart={(e)=>{setDrag(e);}}key={i}>{i+1}</div>)
        }
        return arr
    }
    return (
    <div className='searchBody'>
        <div className="mmm">
            {fav}
        </div>
        <div className="kk" onDragEnter={()=>{
            sC(count+1);
            const y=fav
